// ============================================================
// ULTIMATE WEAPONIZED SMS SYSTEM - 3 TEMPLATES BY LEAD TYPE
// ============================================================
const lead = $input.item.json;

// === EXTRACT FIRST NAME ===
let firstName = 'there';
if (lead.owner_name) {
  const nameParts = lead.owner_name.trim().split(/\s+/);
  firstName = nameParts[0];
  // Handle ALL CAPS names - convert to Title Case
  if (firstName === firstName.toUpperCase() && firstName.length > 1) {
    firstName = firstName.charAt(0).toUpperCase() + firstName.slice(1).toLowerCase();
  }
}

// === FORMAT PHONE NUMBER ===
const phone = lead.phone || lead.phone_1 || lead.phone_2;
let formattedPhone = phone ? phone.replace(/\D/g, '') : null;
if (formattedPhone && formattedPhone.length === 10) {
  formattedPhone = '1' + formattedPhone;
}
if (formattedPhone && !formattedPhone.startsWith('+')) {
  formattedPhone = '+' + formattedPhone;
}

// === FORMAT CURRENCY AMOUNTS ===
const excessAmount = lead.excess_funds_amount || 0;
const formattedExcess = excessAmount.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 2 });

const distressedPrice = lead.distressed_listing_price || 0;
const formattedDistressed = distressedPrice.toLocaleString('en-US', { minimumFractionDigits: 0, maximumFractionDigits: 0 });

const combinedValue = lead.combined_opportunity_value || (excessAmount + distressedPrice);
const formattedCombined = combinedValue.toLocaleString('en-US', { minimumFractionDigits: 0, maximumFractionDigits: 0 });

// === GET COUNTY NAME ===
const county = lead.source_county || 'Dallas';

// === FORMAT CASE NUMBER ===
const caseNumber = lead.case_number || null;

// === FORMAT EXPIRY DATE ===
let expiryText = 'soon';
if (lead.expiry_date) {
  const expiryDate = new Date(lead.expiry_date);
  expiryText = expiryDate.toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' });
} else if (lead.sale_date) {
  const saleDate = new Date(lead.sale_date);
  const estimatedExpiry = new Date(saleDate);
  estimatedExpiry.setFullYear(estimatedExpiry.getFullYear() + 2);
  const now = new Date();
  const daysUntilExpiry = Math.ceil((estimatedExpiry - now) / (1000 * 60 * 60 * 24));
  if (daysUntilExpiry > 0 && daysUntilExpiry <= 365) {
    expiryText = `in ${daysUntilExpiry} days`;
  }
}

// === PROPERTY ADDRESS ===
const propertyAddress = lead.property_address || 'your property';

// === DETERMINE LEAD TYPE AND BUILD MESSAGE ===
let message = '';
let templateType = '';

// TEMPLATE 1: SUPER GOLDEN (has excess funds + distressed property)
if (lead.is_super_golden && distressedPrice > 0 && excessAmount > 0) {
  templateType = 'SUPER_GOLDEN';
  const caseText = caseNumber ? `Case #${caseNumber}` : '';

  message = `Hi ${firstName}, this is Max with MaxSam Recovery. I have two things for you:\n\n` +
    `1) ${county} County owes you $${formattedExcess} from the sale of ${propertyAddress}${caseText ? ` (${caseText})` : ''}. Expires ${expiryText}.\n\n` +
    `2) Your property - we have cash buyers ready at $${formattedDistressed}. Close in 10 days.\n\n` +
    `That's $${formattedCombined}+ back in your pocket.\n\n` +
    `Two short agreements. No upfront costs. No banks. No waiting.\n\n` +
    `Reply:\n` +
    `1 = Claim $${formattedExcess}\n` +
    `2 = Sell property\n` +
    `3 = BOTH (most clients choose this)\n` +
    `STOP = opt out`;
}
// TEMPLATE 2: GOLDEN WITH EXCESS FUNDS (has money owed, no distressed property)
else if (excessAmount > 0) {
  templateType = 'GOLDEN_EXCESS';
  const caseText = caseNumber ? ` Case #${caseNumber}.` : '';

  message = `Hi ${firstName}, Max here from MaxSam Recovery. ${county} County is holding $${formattedExcess} in YOUR name from ${propertyAddress}.${caseText} Expires ${expiryText} - sincerely, not a scam, this goes to the state if unclaimed. We recover these funds for TX families, no cost unless you get paid. Reply 1 to claim your $${formattedExcess}, 2 for questions, STOP to opt out.`;
}
// TEMPLATE 3: GOLDEN HIGH SCORE (high Eleanor score, potential distress signals)
else {
  templateType = 'GOLDEN_HIGH_SCORE';

  message = `Hi ${firstName}, this is Max with MaxSam Recovery. We work with ${county} County property owners and noticed ${propertyAddress} may qualify for our recovery program. Are you the owner or heir? Reply YES if interested, STOP to opt out.`;
}

return {
  ...lead,
  first_name: firstName,
  formatted_phone: formattedPhone,
  formatted_excess: formattedExcess,
  formatted_distressed: formattedDistressed,
  formatted_combined: formattedCombined,
  county: county,
  case_number: caseNumber,
  expiry_text: expiryText,
  template_type: templateType,
  sms_message: message,
  can_send: !!formattedPhone
};

// Count successful sends and breakdown by template type
const allItems = $input.all();
const successCount = allItems.length;

// Calculate totals and breakdown
let totalExcess = 0;
let totalCombined = 0;
let superGoldenCount = 0;
let goldenExcessCount = 0;
let goldenHighScoreCount = 0;

for (const item of allItems) {
  totalExcess += item.json.excess_funds_amount || 0;
  totalCombined += item.json.combined_opportunity_value || item.json.excess_funds_amount || 0;

  switch (item.json.template_type) {
    case 'SUPER_GOLDEN': superGoldenCount++; break;
    case 'GOLDEN_EXCESS': goldenExcessCount++; break;
    case 'GOLDEN_HIGH_SCORE': goldenHighScoreCount++; break;
  }
}

const formattedExcess = totalExcess.toLocaleString('en-US', { style: 'currency', currency: 'USD', minimumFractionDigits: 0 });
const formattedCombined = totalCombined.toLocaleString('en-US', { style: 'currency', currency: 'USD', minimumFractionDigits: 0 });
const formattedFee = (totalExcess * 0.25).toLocaleString('en-US', { style: 'currency', currency: 'USD', minimumFractionDigits: 0 });

// Build summary message for Telegram
const now = new Date();
const dateStr = now.toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' });

const message = `ðŸ¤– SAM Daily Outreach Report\n\n` +
  `ðŸ“… ${dateStr}\n\n` +
  `ðŸ“± LEADS CONTACTED: ${successCount}\n` +
  `â”œ ðŸŒŸ Super Golden (dual): ${superGoldenCount}\n` +
  `â”œ â­ Golden Excess: ${goldenExcessCount}\n` +
  `â”” ðŸ’« Golden High Score: ${goldenHighScoreCount}\n\n` +
  `ðŸ’° OPPORTUNITY VALUE\n` +
  `â”œ Excess Funds: ${formattedExcess}\n` +
  `â”œ Combined (w/ property): ${formattedCombined}\n` +
  `â”” Potential Fee (25%): ${formattedFee}\n\n` +
  `âœ… All leads updated to 'contacted' status.\n\n` +
  `Next batch: Tomorrow 9 AM CST`;

return {
  success_count: successCount,
  super_golden_count: superGoldenCount,
  golden_excess_count: goldenExcessCount,
  golden_high_score_count: goldenHighScoreCount,
  total_excess: totalExcess,
  total_combined: totalCombined,
  telegram_message: message
};

// Log skipped lead (no valid phone)
const lead = $input.item.json;

console.log(`Skipped lead ${lead.id} - ${lead.owner_name}: No valid phone number`);

return {
  skipped: true,
  lead_id: lead.id,
  owner_name: lead.owner_name,
  reason: 'No valid phone number'
};
