// Extract URL from ALEX response
const alexResponse = $input.first().json;
const answer = alexResponse.answer || '';
const routing = alexResponse.routing || {};

// Try to extract URL from the answer
const urlMatch = answer.match(/https?:\/\/[^\s<>"]+/);
const extractedUrl = urlMatch ? urlMatch[0] : null;

return {
  json: {
    county_name: routing.county || 'Unknown',
    notebook_used: routing.notebook || 'Unknown',
    region: routing.region || 'Unknown',
    used_default_notebook: routing.used_default || false,
    alex_answer: answer,
    alex_url: extractedUrl,
    alex_cached: alexResponse.cached || false,
    alex_sources: alexResponse.sources || [],
    // Fall back to Google Sheet URL if ALEX doesn't have one
    use_fallback: !extractedUrl
  }
};
